function isElementInViewport(e){var l=e.getBoundingClientRect();return console.log(l,e),0<=l.top&&l.bottom<=(window.innerHeight||document.documentElement.clientHeight)}function handleScroll(){document.querySelectorAll(".unvisible").forEach(e=>{isElementInViewport(e)?(e.classList.add("visible"),e.classList.remove("unvisible")):(e.classList.add("unvisible"),e.classList.remove("visible"))})}window.addEventListener("scroll",handleScroll),handleScroll();