function isElementInViewport(e){e=e.getBoundingClientRect();return-300<=e.top&&e.bottom<=(window.innerHeight||document.documentElement.clientHeight)+200}function handleScroll(){document.querySelectorAll(".invisible").forEach(e=>{isElementInViewport(e)&&(e.classList.add("visible"),e.classList.remove("invisible"))})}function handleBurgerBtn(e){var n=document.querySelector(".burger_btn_line"),t=document.querySelector(".nav_list");n.classList.toggle("burger_active"),t.classList.toggle("nav_visible")}window.addEventListener("scroll",handleScroll),handleScroll();const btn=document.getElementsByClassName("burger_btn_conteiner")[0];btn.addEventListener("click",handleBurgerBtn);