function isElementInViewport(e){e=e.getBoundingClientRect();return-300<=e.top&&e.bottom<=(window.innerHeight||document.documentElement.clientHeight)+200}function handleScroll(){var e=document.querySelectorAll(".unvisible");[...document.querySelectorAll(".visible"),...e].forEach(e=>{isElementInViewport(e)?(e.classList.add("visible"),e.classList.remove("unvisible")):(e.classList.remove("visible"),e.classList.add("unvisible"))})}function handleBurgerBtn(e){var t=document.querySelector(".burger_btn_line"),n=document.querySelector(".nav_list");t.classList.toggle("burger_active"),n.classList.toggle("nav_visible")}window.addEventListener("scroll",handleScroll),handleScroll();const btn=document.getElementsByClassName("burger_btn_conteiner")[0];btn.addEventListener("click",handleBurgerBtn);