function isElementInViewport(e){e=e.getBoundingClientRect();return 0<=e.top&&e.bottom<=(window.innerHeight||document.documentElement.clientHeight)}function handleScroll(){var e=document.querySelectorAll(".unvisible");[...document.querySelectorAll(".visible"),...e].forEach(e=>{isElementInViewport(e)?(e.classList.add("visible"),e.classList.remove("unvisible")):(e.classList.remove("visible"),e.classList.add("unvisible"))})}function handleBurgerBtn(e){var t=document.querySelector(".burger_btn_line"),n=document.querySelector(".nav_list");t.classList.toggle("burger_active"),n.classList.toggle("nav_visible")}window.addEventListener("scroll",handleScroll),handleScroll();const btn=document.getElementsByClassName("burger_btn_conteiner")[0];function initMap(){var e={lat:49.47323,lng:24.13525};const t=new google.maps.Map(document.getElementById("map"),{center:e,zoom:15,styles:[{featureType:"all",elementType:"all",stylers:[{saturation:-100}]}]}),n=new google.maps.Marker({position:e,map:t,title:"Мое местоположение"}),l=new google.maps.InfoWindow({content:"Фокус, фотосалон"});n.addListener("click",()=>{l.open(t,n)})}btn.addEventListener("click",handleBurgerBtn);